mapper.update:
UPDATE
    sys_code
SET
    audioPath = #{audioPath}
WHERE
    deviceId = #{deviceId}
    AND sessionId = #{sessionId}
    AND code = #{code}

UPDATE sys_device
state = #{state}, deviceName = #{deviceName}, wifiName = #{wifiName}, chipModelName = #{chipModelName}, type = #{type}, version = #{version}, ip = #{ip}, lastLogin = NOW(), roleId = #{roleId},
WHERE 1 = 1 AND userId = #{userId} AND deviceId = #{deviceId}
